mapper.select:
select qnaNum, ansNum, ansContent, ansTime,userid
from answer
where qnaNum = #{num}

select qnaNum, queTitle, queContent, queTime,userid
from question
where qnaNum = #{qnaNum}

select count(*)
from answer
where qnaNum=#{qnaNum}

mapper.update:
        update question
        set queContent=#{questionDto.queContent}, queTitle=#{questionDto.queTitle}
		where qnaNum=#{qnaNum}
